import {ACTIONS} from '../App'

export default function operatorButton({dispatch, digit}){
    return (<button onClick={() => dispatch( {type: ACTIONS.ADD_OPERATOR, payload: {digit}} )}>{digit}</button>)
    //this has returned a button that displays the digit that will be pressed, and when it is clicked, we will call dispatch
}
