import './App.css';
import { useReducer } from 'react';

//all the redux and react code

//all the actions
const ACTIONS = {
    ADD_DIGIT: 'addDigit', //this is used to add a digit to the screen
    ADD_OPERATOR: 'addOperator', //this is used to add an operator to the screen
    CLEAR_ACTION: 'clearAction',
    DELETE_ACTION: 'deleteAction',
    EQUALS_ACTION: 'equalsAction'
}
function reducer(state, {type, payload}){
    switch(type){
        case ACTIONS.ADD_DIGIT:
            return {
                ...state, //returns the previous state and chnages the current value to add the new digit on the end
            currentValue: `${currentValue}${payload.digit}`}
    }
}


//code for the actual app
function App(){
    const [{ currentValue, previousValue, operation }, dispatch] = useReducer(reducer, {})
    return (
        <div className="calculatorGrid"> 
            <div className="outputBox">
                <div className="previousValue">{previousValue} {operation}</div>
                <div className="currentValue">{currentValue}</div>
            </div>
            <button className="largerButtons">AC</button>
            <button>DEL</button>
            <button className="operators">รท</button>
            <button className="button">7</button>
            <button>8</button>
            <button>9</button>
            <button className="operators">ร</button>
            <button>4</button>
            <button>5</button>
            <button>6</button>
            <button className="operators">-</button>
            <button>1</button>
            <button>2</button>
            <button>3</button>
            <button className="operators">+</button>
            <button className="largerButtons">0</button>
            <button>.</button>
            <button className="operators">=</button>

        </div>
    )
}

export default App
